import React, { useState } from "react";
import Cart from "../components/cart";
import { ApolloProvider, ApolloClient, HttpLink, InMemoryCache } from "@apollo/client";
import RestaurantList from "../components/restaurantList";
import { InputGroup, InputGroupAddon, Input } from "reactstrap";

function Home() {
  const API_URL = "https://urchin-app-hi2hs.ondigitalocean.app";
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });

  return (
    <ApolloProvider client={client}>
      <div id="background">
        <div className="container">
          <div className="search">
            <h2>Local Restaurants</h2>
            <InputGroup>
              <InputGroupAddon addonType="append">Search</InputGroupAddon>
              <Input
                onChange={(e) => setQuery(e.target.value.toLocaleLowerCase())}
                value={query}
                className="search-input"
              />
            </InputGroup>
          </div>
          <RestaurantList search={query} />
          <Cart />
        </div>
      </div>
      <style jsx>{`
        #background {
          background: url("https://urchin-app-hi2hs.ondigitalocean.app/uploads/e04b18ac28614e2d8cc3ad94406e08f0.jpg") center/cover no-repeat;
          min-height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
          padding: 20px;
        }

        .container {
          width: 110%; /* Make the container 10% wider */
          max-width: 990px; /* Keep the maximum width */
          background-color: rgba(255, 255, 255, 0.6); /* Set background color with opacity */
          padding: 20px;
          box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.2);
        }

        .search {
          margin-bottom: 20px;
          padding: 10px;
        }

        h2 {
          font-size: 24px;
          margin-bottom: 10px;
          color: #333;
        }

        .search-input {
          width: 100%;
          font-size: 16px;
          background-color: white;
          color: #333;
          border: 1px solid #ccc;
          border-radius: 4px;
          padding: 5px 10px;
        }
      `}</style>
    </ApolloProvider>
  );
}

export default Home;
