import React, { useState } from "react";
import Cart from "../components/cart";
import { ApolloProvider, ApolloClient, HttpLink, InMemoryCache } from "@apollo/client";
import RestaurantList from "../components/restaurantList";
import { InputGroup, InputGroupAddon, Input } from "reactstrap";

function Home() {
  const API_URL = "https://urchin-app-hi2hs.ondigitalocean.app";
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });

  return (
    <ApolloProvider client={client}>
      <div id="background">
        <div className="container">
          <div className="search">
            <h1 style={{ margin: 0, color: "white", textAlign: "left", fontWeight: "bold" , backgroundColor: "black"}}>TownTantalize</h1>

        <br></br>
            <InputGroup>
              <InputGroupAddon addonType="append">Search</InputGroupAddon>
              <Input
                onChange={(e) => setQuery(e.target.value.toLowerCase())}
                value={query}
                className="search-input"
                style={{ backgroundColor: "white", color: "black" }}
              />
            </InputGroup>
          </div>
          <RestaurantList search={query} />
          <Cart />
        </div>
      </div>
      <style jsx>{`
        #background {
          background: url("https://urchin-app-hi2hs.ondigitalocean.app/uploads/cef79037739b47a18308598bd6a42962.jpg") center/cover no-repeat;
  background-size: cover;
            background-repeat: no-repeat;
            background-attachment: fixed;
            background-position: center center;
            min-height: 100vh;
          }
        }

        .container {
          width: 100%;
          max-width: 990px;
          background-color: rgba(255, 255, 255, 0.10);
          padding: 20px;
          box-shadow: 0px 0px 10px rgba(0, 0, 0, 0); /* Add some shadow */
        }

        .search {
          margin-bottom: 20px;
          padding: 10px;
        }

        h2 {
          font-size: 24px;
          margin-bottom: 10px;
        }

        .search-input {
          width: 100%;
          font-size: 16px;
          background-color: white;
          border: 2px solid #ccc;
          border-radius: 4px;
          padding: 5px 10px;
        }
      `}</style>
    </ApolloProvider>
  );
}

export default Home;
