import React, { useState, useEffect, useContext } from "react";
import { useRouter } from "next/router";
import {
  Container,
  Row,
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
} from "reactstrap";
import { login } from "../components/auth";
import AppContext from "../components/context";

function Login(props) {
  const [data, updateData] = useState({ identifier: "", password: "" });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const router = useRouter();
  const appContext = useContext(AppContext);

  useEffect(() => {
    if (appContext.isAuthenticated) {
      router.push("/"); // redirect if you're already logged in
    }
  }, []);

  function onChange(event) {
    updateData({ ...data, [event.target.name]: event.target.value });
  }

  return (
    <Container>
      <Row>
        <Col sm="12" md={{ size: 5, offset: 3 }}>
          <div className="login-background">
            <div className="paper">
              <div className="header">
                <img
                  src="https://urchin-app-hi2hs.ondigitalocean.app/uploads/e98c2d51d2b24d08a390bd71822f86f3.jpg"
                  width={90}
                  height={90}
                  alt="Logo"
                />
                <h2>Login</h2>
              </div>
              <section className="wrapper">
                {/* Rest of your component */}
              </section>
            </div>
          </div>
        </Col>
      </Row>
      <style jsx>
        {`
          .login-background {
            background-image: url("https://urchin-app-hi2hs.ondigitalocean.app/uploads/cef79037739b47a18308598bd6a42962.jpg"); /* Replace with your image path */
            background-size: fill; /* Adjust the background size as needed */
            background-repeat: no-repeat;
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
          }

          /* Rest of your CSS styles */
        `}
      </style>
    </Container>
  );
}

export default Login;
