import React, { useState, useEffect, useContext } from "react";
import { useRouter } from "next/router";
import {
  Container,
  Row,
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
} from "reactstrap";
import { register } from "../components/auth"; // You may need to import the appropriate registration function
import AppContext from "../components/context";

function Register(props) {
  const [data, updateData] = useState({ /* Your registration form data */ });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);
  const router = useRouter();
  const appContext = useContext(AppContext);

  useEffect(() => {
    if (appContext.isAuthenticated) {
      router.push("/"); // Redirect if the user is already logged in
    }
  }, []);

  function onChange(event) {
    updateData({ ...data, [event.target.name]: event.target.value });
  }

  return (
    <div id="background"> {/* Apply background to this div */}
      <Container>
        <Row>
          <Col sm="12" md={{ size: 5, offset: 3 }}>
            <div className="paper">
              {/* Rest of your registration form code */}
            </div>
          </Col>
        </Row>
      </Container>

      <style jsx>
        {`
          #background {
            background-image: url("https://urchin-app-hi2hs.ondigitalocean.app/uploads/cef79037739b47a18308598bd6a42962.jpg");
            background-size: cover;
            background-repeat: no-repeat;
            min-height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
            padding: 20px;
          }
          .paper {
            border: 1px solid lightgray;
            box-shadow: 0px 1px 3px 0px rgba(0, 0, 0, 0.2),
              0px 1px 1px 0px rgba(0, 0, 0, 0.14),
              0px 2px 1px -1px rgba(0, 0, 0, 0.12);
            border-radius: 6px 6px 0 0; /* Rounded top corners */
            margin-top: 90px;
            background-color: #f8f8f8; /* Beige background color */
          }
          /* Rest of your CSS styles */
        `}
      </style>
    </div>
  );
}

export default Register;
