import React, { useState } from "react";
import Cart from "../components/cart";
import { ApolloProvider, ApolloClient, HttpLink, InMemoryCache } from "@apollo/client";
import RestaurantList from "../components/restaurantList";
import { InputGroup, InputGroupAddon, Input } from "reactstrap";

function Home() {
  const API_URL = "https://urchin-app-hi2hs.ondigitalocean.app";
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql` });
  const cache = new InMemoryCache();
  const client = new ApolloClient({ link, cache });

  return (
    <ApolloProvider client={client}>
    <div id="background">
  <div class="container">
    <div class="search">
      <h1 class="header-title">TownTantalize</h1>
      <br />
      <div class="input-group">
        <div class="input-group-prepend">
          <span class="input-group-text">Search</span>
        </div>
        <input
          type="text"
          class="form-control search-input"
          placeholder="Enter your search query..."
          onChange={(e) => setQuery(e.target.value.toLowerCase())}
          value={query}
        />
      </div>
    </div>
    <RestaurantList search={query} />
    <br></br>
    <Cart />
  </div>
</div>

      <style jsx>{`
        #background {
          background: url("https://urchin-app-hi2hs.ondigitalocean.app/uploads/cef79037739b47a18308598bd6a42962.jpg") center/cover no-repeat;
            background-size: cover;
            background-repeat: no-repeat;
            background-attachment: fixed;
            background-position: center center;
            min-height: 100vh;
          }

          .container {
            background-color: rgba(255, 255, 255, 0.8); /* Semi-transparent white background */
            padding: 20px;
            border-radius: 10px;
            box-shadow: 0 0 10px rgba(0, 0, 0, 0.3);
            margin-top: 20px;
          }

          .header-title {
            font-size: 36px;
            color: #333; /* Dark text color */
            text-align: left;
            font-weight: bold;
            padding: 10px 0;
          }

          .search {
            text-align: center;
            padding: 20px 0;
          }

          .input-group {
            width: 100%;
            max-width: 400px; /* Adjust the width as needed */
            margin: 0 auto;
          }

          .input-group-text {
            background-color: #333; /* Dark background color for the search button */
            color: white;
          }

          .search-input {
            background-color: #fff; /* White background for the input field */
            color: #333; /* Dark text color */
          }

          /* Add more styles as needed for RestaurantList and Cart components */

        }
      `}</style>
    </ApolloProvider>
  );
}

export default Home;
