import React, { useState } from "react";
import Cart from "../components/cart";
import { ApolloProvider, ApolloClient, HttpLink, InMemoryCache } from '@apollo/client';
import RestaurantList from '../components/restaurantList';
import { InputGroup, InputGroupAddon, Input } from "reactstrap";

function Home() {
  const API_URL = "https://urchin-app-hi2hs.ondigitalocean.app";
  const [query, setQuery] = useState("");
  const link = new HttpLink({ uri: `${API_URL}/graphql` })
  const cache = new InMemoryCache()
  const client = new ApolloClient({ link, cache });

  return (
    <ApolloProvider client={client}>
      <div className="container">
        <div className="search">
          <h2>Local Restaurants</h2>
          <InputGroup>
            <InputGroupAddon addonType="append">Search</InputGroupAddon>
            <Input
              onChange={(e) =>
                setQuery(e.target.value.toLocaleLowerCase())
              }
              value={query}
              className="search-input"
            />
          </InputGroup>
        </div>
        <RestaurantList search={query} />
        <Cart />
      </div>
      <style jsx>{`
        .container {
          max-width: 800px;
          margin: 0 auto;
          padding: 20px;
          background: url('https://example.com/your-image-url.jpg') center/cover no-repeat; // Add your image URL here
        }

        .search {
          margin-bottom: 20px;
          background-color: rgba(255, 255, 255, 0.8); // Add a light background color with opacity
          padding: 10px;
        }

        h2 {
          font-size: 24px;
          margin-bottom: 10px;
          color: #333;
        }

        .search-input {
          width: 100%;
          font-size: 16px;
          background-color: white;
          color: #333;
          border: 1px solid #ccc;
          border-radius: 4px;
          padding: 5px 10px;
        }
      `}</style>
    </ApolloProvider>
  );
}

export default Home;
